CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_taatencion_consultas_no_atendidas` ()  BEGIN
	SELECT A.int_nro_atencion as nroatencion, A.dtt_fecha_atiende as fechaatencion,
           A.int_idservicio as idservicio, S.vch_denominacion as denominacion, A.chr_dni_paciente as idpaciente,
		   concat(P.vch_nombres, ' ', P.vch_apellidos) as nombre, P.chr_doc_id as dni
	FROM taatencion A INNER JOIN tapaciente P ON A.chr_dni_paciente = P.chr_dni_paciente
		 INNER JOIN taservicio S ON A.int_idservicio = S.int_idservicio
	WHERE A.vch_estado = "no atendido" AND (S.vch_tipo = "Consulta" OR S.vch_tipo = "Procedimiento")
	ORDER BY A.int_nro_atencion;
END$$

