CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_tapaciente_estadistico` (IN `pfechainicio` DATE, IN `pfechafin` DATE)  BEGIN
  (select pfechainicio as fecha, count(chr_dni_paciente) as cuenta
  from tapaciente
  where (date(dat_fecha_filiacion) <= pfechainicio))
  union all
  (select date(dat_fecha_filiacion) as fecha, count(*) as cuenta
  from tapaciente
  where (date(dat_fecha_filiacion) between date(pfechainicio + 1) and pfechafin)
  group by date(dat_fecha_filiacion));
END$$

