CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_taservicio_actualizar` (IN `pidservicio` INT, IN `pdenominacion` VARCHAR(100), IN `pdescripcion` VARCHAR(100), IN `pprecio` DECIMAL(10,2), IN `ptipo` VARCHAR(45), IN `pformato` LONGTEXT, IN `pconsultorio` INT)  BEGIN
  IF NOT EXISTS (SELECT * FROM taservicio WHERE int_idservicio = pidservicio)
  THEN
	INSERT INTO taservicio (int_idservicio, vch_denominacion, vch_descripcion, dbl_precio, vch_tipo, vch_formato, int_consultorio)
	VALUES (pidservicio, pdenominacion, pdescripcion, pprecio, ptipo, pformato, pconsultorio);
  ELSE
	UPDATE taservicio
    SET vch_denominacion = pdenominacion, vch_descripcion = pdescripcion, dbl_precio = pprecio,
		vch_tipo = ptipo, vch_formato = pformato, int_consultorio = pconsultorio
