CREATE DEFINER=`root`@`localhost` PROCEDURE `sp_taservicio_listar_servicio` (IN `pservicio` VARCHAR(100), IN `pconsulta` VARCHAR(45), IN `pprocedimiento` VARCHAR(45), IN `pexamen` VARCHAR(45))  BEGIN
  IF (pconsulta = '' AND pprocedimiento = '' AND pexamen = '')
  THEN
     SELECT vch_denominacion as Servicio, vch_descripcion as Descripcion, dbl_precio as Precio,int_idservicio as ID
    FROM taservicio
    WHERE int_idservicio > 3
	AND (vch_denominacion LIKE concat('%',pservicio,'%'));
  ELSE
	 SELECT vch_denominacion as Servicio, vch_descripcion as Descripcion, dbl_precio as Precio, int_idservicio as ID
    FROM taservicio
    WHERE int_idservicio > 3
	AND (vch_denominacion LIKE concat('%',pservicio,'%'))
    AND (vch_tipo=pconsulta OR vch_tipo=pprocedimiento OR vch_tipo=pexamen);
  END IF;
END$$

